import React, { useState,useEffect} from 'react'
import { View, Text, StyleSheet, TextInput, Platform, TouchableOpacity, ScrollView, FlatList,Image } from 'react-native'
import AsyncStorage from '@react-native-async-storage/async-storage'
import  { Button }  from '../components/Button'
import { SkillCard } from '../components/SkillCard'
interface Cadastro{
    id: string,
    codigo: string,
    pais: string,
    
}


export function Home() {
    const [newCodigo, setNewCodigo] = useState('')
    const [myCodigo, setMyCodigo] = useState<Cadastro[]>([])
    const [newPais, setNewPais] = useState('')
    const [myPais, setMyPais] = useState<Cadastro[]>([])
    const [greeting, setGreeting] = useState('')

    function handleAddNew(){
        const Dati = {
            id: String(new Date().getTime()),
            codigo: newCodigo,
            pais: newPais,
            
        }
        setMyCodigo([...myCodigo,Dati])
        setNewCodigo('')
        setMyPais([...myPais,Dati])
        setNewPais('')
    }

    function handleRemove(id: string){
        setMyCodigo(myCodigo.filter(Cadastro=> Cadastro.id !== id))

    }

    useEffect(() => {
        const currentHour = new Date().getHours();
        if (currentHour >=5 && currentHour < 12){
            setGreeting('Bom dia')
        }else if (currentHour >= 12 && currentHour < 18) {
            setGreeting('Boa Tarde')
        } else {
            setGreeting('Boa Noite')
        }
    }, [])

    useEffect(() => {
        async function loadData() {
            const storagedSkills = await AsyncStorage.getItem('@myCodigo:Codigo')
            if(storagedSkills){
                setMyCodigo(JSON.parse(storagedSkills))
            }
        }
        loadData()
    }, [])
    
    
    useEffect(() => {
        async function removeAll() {
            await AsyncStorage.removeItem('@myCodigo:Codigo')
        }
        removeAll()
    }, [])    

    useEffect (() =>{
        async function saveData(){
            await AsyncStorage.setItem('@myCodigo:Codigo', JSON.stringify(myCodigo))
        }
        saveData()
    },[myCodigo])

  return(
    <>
        <View style={styles.container}>
            <Image style={{width: 300, height: 60 ,alignSelf: 'center', justifyContent: 'center'}} source={require('../assets/Rocket.png')}/>
          
            <Text style={[styles.greetings, {textAlign:'center'}]}>
                {greeting}! Seja Muito bem vindo a maior comunidade
            </Text>
            <Text style={[styles.greetings,{textAlign:'center'}]}>
            Dev do Brasil!
            </Text>

            <Text style={[styles.title,{textAlign: 'center'}]}>Cadastre suas informações abaixo.</Text>
        
            <TextInput
            style={styles.input}
            placeholder= 'Código do País'
            value={newCodigo}
            placeholderTextColor='#ffffff7a'
            onChangeText={value => setNewCodigo(value)}
            />

            <TextInput
            style={styles.input}
            placeholder= 'Nome do Pais'
            value={newPais}
            placeholderTextColor='#ffffff7a'
            onChangeText={value => setNewPais(value)}
            />

            

            <Button 
            onPress={handleAddNew}
            title = 'Cadastrar'
            />

                       
            <Text style={[styles.title]}>
                Países Cadastradas :
            </Text>
            
            <FlatList showsVerticalScrollIndicator={false}
            data={myCodigo}
            keyExtractor={item=> item.id}
            renderItem={({item})=> ( 
                <SkillCard
                Codigo={item.codigo}
                Pais={item.pais}
                onPress={() => handleRemove(item.id)}
                />
                
            )}
            />
            <Button
            data={myCodigo}
            onPress={() => handleRemove(item.id)}
            title = 'Cadastrar'
            />
            
        </View>
    </>
  )
}

const styles= StyleSheet.create({
    container: {
        flex:1,
        backgroundColor: '#121214',
        paddingHorizontal:30,
        paddingVertical: 70
    },
    title: {
        color:'#fff',
        fontSize:25,
        fontWeight: 'bold'
    },
    input:{
        backgroundColor:'#1f1e25',
        color: '#fff',
        fontSize: 18,
        padding: Platform.OS =='ios' ? 15 : 12,
        marginTop: 5,
        borderRadius: 30
    },
    greetings: {
        color: '#fff',
        fontSize:20,
    }, 
})
